import { useState } from "react"

const Signup = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [ime, setIme] = useState('')
  const [prezime, serPrezime] = useState('')
  const [brojTel, setBrojTel] = useState('')
  const [adresa, setAdresa] = useState('')
  const [datum_zaposlenja, setDatumZaposlenja] = useState('')
  const [datum_otkaza, serDatumOtkaza] = useState('')

  const handleSubmit = async (e) => {
    e.preventDefault()

    console.log(email, password,ime)
  }

  return (
    <form className="signup" onSubmit={handleSubmit}>
        <div className="nesto" style={{  
            height:8500,
            backgroundImage: "url(" + "https://img.freepik.com/free-vector/smooth-white-wave-background_52683-55288.jpg?w=2000" + ")",
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
      <h3>Sign Up</h3>
      
      <label>Ime:</label>
      <input 
        type="text" 
        onChange={(e) => setIme(e.target.value)} 
        value={ime} 
      />


      <label>Prezime:</label>
      <input 
        type="text" 
        onChange={(e) => serPrezime(e.target.value)} 
        value={prezime} 
      />

<label>Email address:</label>
      <input 
        type="email" 
        onChange={(e) => setEmail(e.target.value)} 
        value={email} 
      />
      <label>Password:</label>
      <input 
        type="password" 
        onChange={(e) => setPassword(e.target.value)} 
        value={password} 
      />


      <label>Broj telefona:</label>
      <input 
        type="text" 
        onChange={(e) => setBrojTel(e.target.value)} 
        value={brojTel} 
      />
      <label>Adresa:</label>
      <input 
        type="text" 
        onChange={(e) => setAdresa(e.target.value)} 
        value={adresa} 
      />
      <label>Datum zaposlenja:</label>
      <input 
        type="date" 
        onChange={(e) => setDatumZaposlenja(e.target.value)} 
        value={datum_zaposlenja} 
      />
      <label>Datum otkaza:</label>
      <input 
        type="date" 
        onChange={(e) => serDatumOtkaza(e.target.value)} 
        value={datum_otkaza} 
      />


      

      <button>Sign up</button>
      </div>
    </form>
  )
}

export default Signup
