import { useEffect,useState } from "react"

const Home=()=>{
const [sirovina,setSirovina]=useState(null);

useEffect(()=>{
const fetchSirovine=async()=>{
const response=await fetch('http://localhost:4000/api/sirovine')
const json=await response.json();

if(response.ok){
setSirovina(json);
}


}
fetchSirovine();
},[])




    return (
        <div className="home"> 
            <div className="sirovina">
                {sirovina &&sirovina.map((sirovina)=>(
                    <p key={sirovina._id}>{sirovina.cijena}</p>
                ))}
            </div>
        </div>
    )
}

export default Home
