import { useEffect,useState } from "react"

//components
import DobavljacDetalji from "../components/DobavljaciDetalji"
import { useDobavljacKontext } from "../hooks/koristitDobavljacKontekst"


const DobavljacPage = () => {
    //const [sirovina, setSirovina] = useState(null)
   const {dobavljac,dispatch}= useDobavljacKontext()
    useEffect(() => {
      const fettchDobavljac = async () => {
        const response = await fetch('/api/dobavljaci')
        const json = await response.json()
  
        if (response.ok) {
          dispatch({type:'SET_DOBAVLJAC',payload:json})
             //setSirovina(json)
         }
      }
  
      fettchDobavljac()
    }, [])


    return (
<div className="home"


>    <div className="workouts"


>
      {dobavljac &&dobavljac.map((dob)=>(
           <DobavljacDetalji key={dob._id} dobavljac={dob}/>
        ))}
      </div>
      
    </div>




    )
}

export default DobavljacPage
