import { createContext, useReducer } from "react";



export const DobavljacContext=createContext();

export const dobavljacReducer=(state,action)=>{
    switch(action.type){
        case 'SET_DOBAVLJAC':
            return {
                dobavljac:action.payload
            }
        case 'CREATE_DOBAVLJAC':
            return{
                dobavljac:[action.payload,...state.sirovina]
            }
        case 'DELETE_DOBAVLJAC':
            return{
                dobavljac:state.dobavljac.filter((w)=>w._id!==action.payload._id)
            }

        default:
            return state
    }
}; 





export const DobavljacKontekstProvider=({children})=>{
const  [state,dispatch]=useReducer(dobavljacReducer,{
    dobavljac:null
})


//dispatch({type:'SET_SIROVINU',payload:[{},{}]})

    return(
    <DobavljacContext.Provider  value={{...state,dispatch}}>
        {children}    
    </DobavljacContext.Provider>
)

}
