import { useDobavljacKontext } from "../hooks/koristitDobavljacKontekst";

const DobavljacDetalji=({dobavljac})=>{
const {dispatch}=useDobavljacKontext();


const handleClick=async()=>{
const response=await fetch('api/dobavljaci/'+dobavljac._id,{
  method:'DELETE',

})

const json=await response.json();

if(response.ok){
dispatch({type:'DELETE_DOBAVLJAC',payload:json})

}if(!response.ok){

}

}

return(
    <div className="sirovine-details"
    style={{  

      backgroundPosition: 'center',
      backgroundSize: 'cover',
      backgroundRepeat: 'no-repeat'
    }}
    >
        <h4>{dobavljac.naziv}</h4>
      
      <span className="material-symbols-outlined" onClick={handleClick}>delete</span>
    </div>
)

}

export default DobavljacDetalji;
