import { useState } from "react";
const FormaSir=()=>{
    const [id,setId]=useState('');
const [naziv,setNaziv]=useState('');
const [kolicina,setKolicina]=useState('');
const [min_kolicina,setMin_Kolicina]=useState('');
const [cijena,setCijena]=useState('');
const [jed_mjere,setjed_mjere]=useState('');
const [korisni,setKorisni]=useState('');
const [dobavljac_id,setDobavljacId]=useState('');

const[eror,setEror]=useState(null);


const handleSubmit=async(e)=>{
        e.preventDefault()
    
    
    
        const sirovina={id, naziv, kolicina, min_kolicina, cijena, jed_mjere, korisni, dobavljac_id}
    const response=await fetch('/api/sirovine/',{
        method:"POST",
        body:JSON.stringify(sirovina),
        headers: {
            'Content-Type': 'application/json'
          }
    
    })
    
    const json=await response.json();
    
    if(!response){
        setEror(json.eror);
    }
    if(response.ok){
        setId('');
        setCijena('');
    
        setEror(null);
        console.log("novi artikl usojesno dodan",json)
    }
    
    }


    return(
        <div>nesto</div>
    )
}

export default FormaSir
