import {BrowserRouter,Route,Routes,Navigate}from 'react-router-dom'
import { Link } from "react-router-dom"
import { useAuthContext } from './hooks/useAuthContext';
//stranice i komponente
import AdminPage from './pages/AdminPage';
import Home from './pages/Home';
import Navnar from './components/navbar';
import Login from './pages/Login';
import Signup from './pages/Signup';
import UserPage from './pages/UserPage';

function App() {

  const {user}=useAuthContext();

  return (
    <div className="App"
    style={{  height:1100 ,
  backgroundImage: "url(" + "https://png.pngtree.com/thumb_back/fh260/background/20200714/pngtree-modern-double-color-futuristic-neon-background-image_351866.jpg " + ")",
  backgroundPosition: 'center',
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat'
}}
    >
      <BrowserRouter>

          <div className='pages'> 
          <Navnar/> 
            <Routes>
            <Route
              path='/user'
              element={  <UserPage/>}
                />
            <Route
              path='/admin'
              element={ user && user.novi==="admin" ? <AdminPage/> :<Navigate to="/user"/> }
                />

              <Route
              path='/sirovine'
              element={ user ? <Home/> : <span>{user.novi}</span>}
                />


            <Route
              path='/login'
              element={!user ? <Login/> : <Navigate to="/sirovine"/>}
                />
                <Route
              path='/register'
              element={!user ?  <Signup/> : <Navigate to="/sirovine"/>}
                />




            </Routes>

          </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
